import { Loading } from "../Loading";
import { StyledBoxModal, StyledModal } from "../Modal/style";
import { VscChromeClose } from "react-icons/vsc";
import { useForm } from "react-hook-form";
import { useContext } from "react";
import { TechContext } from "../../Contexts/TechContext";

export const ModalUpdate = ({ title }) => {
  const { register, handleSubmit } = useForm();
  const { setModalUpdate, loadModalUpdate, submitFormModalUpdate } =
    useContext(TechContext);
  return (
    <StyledModal>
      {loadModalUpdate && <Loading />}
      <StyledBoxModal>
        <header>
          <h2>{title}</h2>
          <button onClick={() => setModalUpdate(false)}>
            <VscChromeClose />
          </button>
        </header>
        <form onSubmit={handleSubmit(submitFormModalUpdate)}>
          <label>Nome</label>
          <input
            type="text"
            placeholder="Digite o nome da tecnologia"
            {...register("title")}
          />
          <label>Selecionar Status</label>
          <select name="" id="" {...register("status")}>
            <option value="">--Selecione</option>
            <option value="Iniciante">Iniciante</option>
            <option value="Intermediário">Intermediário</option>
            <option value="Avançado">Avançado</option>
          </select>
          <button type="submit">Cadastrar Tecnologia</button>
        </form>
      </StyledBoxModal>
    </StyledModal>
  );
};
