import { StyledSectionLogin } from "./style";
import logo from "../../assets/LogoLogin.svg";
import { useForm } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { api } from "../../Services/api";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import { Form } from "../../Components/Form";
import { Inputs } from "../../Components/Inputs";
import { useState } from "react";
import { Loading } from "../../Components/Loading";

export const LoginPage = () => {
  /* const [user, setUser] = useState([]); */
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleNavigateToRegister = () => {
    navigate("/register");
  };

  const formShema = Yup.object().shape({
    email: Yup.string().email().required("Email é obrigatório"),
    password: Yup.string()
      .required("Senha obrigatória")
      .matches(/.{8,}/, "A senha precisa conter no mínimo 8 caracteres"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formShema),
  });

  const onSubmitForm = async (data) => {
    try {
      const response = await api.post("/sessions", data);
      console.log(response);
      setUser([response.data.user]);
      window.localStorage.clear();
      window.localStorage.setItem("@TOKEN", response.data.token);
      window.localStorage.setItem("@USERID", response.data.user.id);
      toast.success("Login efetuado com sucesso");
      setTimeout(() => {
        navigate(`/user/${response.data.user.id}`);
      }, 3000);
    } catch (error) {
      console.log(error);

      toast.error("Algo deu errado");
    } finally {
      setLoading(true);
    }
  };
  return (
    <>
      {loading && <Loading />}
      <StyledSectionLogin>
        <img src={logo} alt="logo" />

        <div>
          <h2>Login</h2>
          <Form submit={onSubmitForm} handle={handleSubmit}>
            <label>Email</label>
            <Inputs
              type="text"
              placeholder="Digite aqui seu email"
              register={register("email")}
            />
            {errors.email?.message && <span>{errors.email.message}</span>}
            <label>Senha</label>
            <Inputs
              type="password"
              placeholder="Digite aqui sua senha"
              register={register("password")}
            />
            {errors.password?.message && <span>{errors.password.message}</span>}
            <button type="submit">Entrar</button>
          </Form>
          <p>Ainda não possui uma conta?</p>
          <button type="submit" onClick={handleNavigateToRegister}>
            Cadastrar
          </button>
        </div>
      </StyledSectionLogin>
      <ToastContainer
        position="top-right"
        autoClose={2000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="dark"
      />
    </>
  );
};
