import styled from "styled-components";

export const StyledMain = styled.main`
  background-color: var(--color-gray-4);
  height: 100vh;

  & > section:first-child {
    border-bottom: 1px solid var(--color-gray-3);
    height: 25%;

    div {
      width: 60%;
      margin: 0 auto;
      display: flex;
      justify-content: space-between;
      align-items: center;
      height: 100%;

      h2 {
        font-size: 18px;
        font-weight: 700;
        color: #f8f9fa;
      }
      span {
        font-size: 12px;
        font-weight: 600;
        line-height: 18px;
        color: var(--color-gray-1);
      }
    }
  }

  section {
    div {
      width: 60%;
      margin: 0 auto;
      display: flex;
      justify-content: space-between;
      align-items: center;
      height: 100%;
    }
  }
`;
