import { StyledSectionRegister } from "./style";
import logo from "../../assets/Logo.svg";
import { useForm } from "react-hook-form";
import * as Yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { number } from "yup/lib/locale";

export const RegisterPage = () => {
  const formShema = Yup.object().shape({
    name: Yup.string().required("Nome obrigatório"),
    email: Yup.string().email().required("Email is required"),
    bio: Yup.string().required("Bio obrigatório"),
    course_module: Yup.string().required("Modulo obrigatório"),
    password: Yup.string()
      .required("Password is required")
      .max(8, "Deve ter no maximo 8 caracteres"),
    passwordConfirmation: Yup.string()
      .required("Senha de confirmação obrigatória")
      .oneOf([Yup.ref("password"), null], "Senhas não são iguais"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formShema),
  });

  const onSubmitForm = (data) => {
    console.log(data);
    /*  try {
    } catch (error) {} */
  };
  return (
    <StyledSectionRegister>
      <div>
        <img src={logo} alt="logo" />
        <button>Voltar</button>
      </div>
      <div>
        <div>
          <h2>Crie sua conta</h2>
          <p>Rapido e grátis, vamos nessa</p>
        </div>
        <form onSubmit={handleSubmit(onSubmitForm)}>
          <label>Nome</label>
          <input
            type="text"
            placeholder="Digite aqui seu nome"
            {...register("name")}
          />
          {errors.name?.message && <span>{errors.name.message}</span>}
          <label>Email</label>
          <input
            type="email"
            placeholder="Digite aqui seu email"
            {...register("email")}
          />
          {errors.email?.message && <span>{errors.email.message}</span>}
          <label>Senha</label>
          <input
            type="password"
            placeholder="Digite aqui sua senha"
            {...register("password")}
          />
          {errors.password?.message && <span>{errors.password.message}</span>}

          <label>Confirmar senha</label>
          <input
            type="password"
            placeholder="Digite novamente sua senha"
            {...register("passwordConfirmation")}
          />
          {errors.passwordConfirmation?.message && (
            <span>{errors.passwordConfirmation.message}</span>
          )}
          <label>Bio</label>
          <input
            type="text"
            placeholder="Fale sobre você"
            {...register("bio")}
          />
          <span>{errors.passwordConfirmation.message}</span>

          <label>Contato</label>
          <input
            type="text"
            placeholder="Opção de contato"
            {...register("contact")}
          />
          {errors.contact?.message}
          <label>Selecionar modulo</label>
          <select name="" id="" {...register("course_module")}>
            <option value="">--Selecione </option>
            <option value="1 Modulo">1 Modulo</option>
            <option value="2 Modulo">2 Modulo</option>
            <option value="3 Modulo">3 Modulo</option>
            <option value="4 Modulo">4 Modulo</option>
          </select>
          {errors.course_module?.message}
          <button type="submit">Cadastrar</button>
        </form>
      </div>
    </StyledSectionRegister>
  );
};
